  display: flex
  justify-content: space-around
  align-items: center
  .navi-item
    width: 1rem
    display: flex
    flex-direction: column
    align-items: center
    justify-content: center
    .navi-item-img
      width: .4rem
      height: .4rem
    .navi-item-text
      margin-top: .12rem
